import React,{ Component } from "react";
import { fetcheverything } from "../../apis/fetcheverything";
export default class GeneralNews extends Component
{
    constructor(props)
    {
        super(props)
        this.state={
            headlines:null
        }
    }
componentDidMount()
{
    const getData = async ()=>
    {
        const data1 = await fetcheverything()
        // console.log(data1)
        this.setState({ headlines: data1.articles })
    }
    getData()
}

render() {
    console.log(this.state.headlines)
    const news = this.state.headlines 
    return (
        <div>
            <h1> General News </h1>
            {news != null && news.length ? 
                news.map((item,index)=>(
                    <div key={index}>
                        <h3>{item.title}</h3>
                        <img src={item.urlToImage} width={250} height={250} alt="" />
                        <p> {item.description} </p>
                    </div>
                ))
            : <span> Loading... </span>}
        </div>
    )
}
}
