import react,{Component} from 'react'
export default class AppInput9_10 extends Component{
    constructor(props)
    {
        super(props);
        this.state={
            'fname':'',
            'lname':'',
            'result':''
        }
        
    }

    handleInputChange=(e)=>{
        const target=e.target;
        const name=target.name;
        const value=target.value;
        this.setState({
            [name]:value
        })
        console.log(this.state.fname);
        console.log(this.state.lname);
    }
    onSubmit=(e)=>{
        const result=parseInt(this.state.fname)+parseInt(this.state.lname)

        this.setState({
            [result]:result
        })
    }
    

    render()
    {
        return(
            <div>
                <input type='text'name='fname' placeholder='enter the firstname'onChange={this.handleInputChange} value={this.state.fname}/>
                <br/><br/>

                <input name='lname'type='text' placeholder='enter the lastname' onChange={this.handleInputChange} value={this.state.lname}/>
                <br/><br/>

                <button onClick={this.onSubmit}>Submit</button><br/>
                <br/>
                <h1>{this.state.fname}{this.state.lname}</h1>
                <h1>{this.state.result}</h1>
            </div>
        );
    }
}
